mapper.select:
SELECT stationId, stationName, xPoint, yPoint
FROM t_stationinfo
WHERE cityId = (SELECT cityId FROM t_city WHERE cityName = #{cityName})
  and xPoint > #{minLng}
  and xPoint < #{maxLng}
  and yPoint > #{minLat}
  and yPoint < #{maxLat}

SELECT t1.busNo, t2.licensePlate
FROM t_road t1,
     t_bus_realrun t2
WHERE t1.stationId = #{stationId}
  AND t1.busNo = t2.busNo
  AND t1.cityId = (SELECT cityId FROM t_city WHERE cityName = #{cityName})
  AND t1.direction = #{direction}
  AND t1.direction = t2.direction;

SELECT t2.travelSort
FROM t_stationinfo t1,
     t_road t2
WHERE t1.stationName = #{stationName}
  and t1.stationId = t2.stationId
  and t2.busNo = #{busNo}
  and t2.direction = #{direction}
  and t1.cityId = (SELECT cityId FROM t_city WHERE cityName = #{cityName})

select *
from t_wx_collect
where stationName = #{stationName}
  and cityName = #{cityName}
  and openId = #{openId}
  and stationId = #{stationId};

select distinct cityName
from t_wx_collect
where openId = #{openId};

select *
from t_wx_collect
where openId = #{openId}
  and cityName = #{cityName};

select *
from t_wx_history
where openId = #{openId}
  and cityName = #{cityName};

mapper.delete:
delete
from t_wx_history
where openId = #{openId}
  and cityName = #{cityName};

mapper.select:
select xPoint, yPoint
from t_stationinfo
where stationName = #{stationName}
  and cityId = (SELECT cityId FROM t_city WHERE cityName = #{cityName});

SELECT stationId as id, stationName, xPoint as longitude, yPoint as latitude
from t_stationinfo
WHERE stationId in (SELECT t2.stationId
                    from t_bus_realrun t1,
                         t_road t2
                    WHERE t1.busNo = t2.busNo
                      and t1.busNo = #{busNo}
                      and t2.direction = t1.direction
                      and t2.direction = #{direction}
                    ORDER BY t2.travelSort)
